function gatherDupes() {
var ga = new GlideAggregate('cmdb_ci');
ga.addAggregate('COUNT', 'name');
ga.addQuery('name', '!=', '');
ga.groupBy('name');
ga.addHaving('COUNT', '>', 1);
ga.query();
var dupNames = [];
gs.print('The Configuration Items table has ' + ga.getRowCount() + ' duplicates based on Name field...');
while (ga.next()) {
gs.print(ga.name);
fixDuplicates(ga.name);
}
}
function fixDuplicates(ciName) {
   var gr = new GlideRecord('cmdb_ci');
   gr.addQuery('name', ciName);
   gr.orderByDesc('sys_created_on');
   gr.query();
while (gr.next()) {
gs.print('Name ' + gr.name + ' Serial Number ' + gr.serial_number + ' Asset Tag ' + gr.asset_tag);
}
}
